import React, { useState, useEffect } from 'react';
import { Space, SeatPlace } from '../types';
import { spacesApi, bookingsApi } from '../services/api';

const SpacesList: React.FC = () => {
  const [spaces, setSpaces] = useState<Space[]>([]);
  const [selectedSpace, setSelectedSpace] = useState<Space | null>(null);
  const [seatPlaces, setSeatPlaces] = useState<SeatPlace[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchSpaces = async () => {
      try {
        const response = await spacesApi.getAllSpaces();
        setSpaces(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch spaces');
        setLoading(false);
      }
    };

    fetchSpaces();
  }, []);

  const handleSpaceClick = async (space: Space) => {
    try {
      const response = await spacesApi.getSeatPlaces(space.id);
      setSeatPlaces(response.data);
      setSelectedSpace(space);
    } catch (err) {
      setError('Failed to fetch seat places');
    }
  };

  const handleBooking = async (seatPlaceId: string) => {
    try {
      await bookingsApi.createBooking({
        seatPlaceId,
        startTime: new Date().toISOString(),
        endTime: new Date(Date.now() + 3600000).toISOString(), // 1 hour from now
      });
      // Refresh seat places after booking
      if (selectedSpace) {
        const response = await spacesApi.getSeatPlaces(selectedSpace.id);
        setSeatPlaces(response.data);
      }
    } catch (err) {
      setError('Failed to create booking');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Available Spaces</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {spaces.map((space) => (
          <div
            key={space.id}
            className="border p-4 rounded-lg cursor-pointer hover:bg-gray-50"
            onClick={() => handleSpaceClick(space)}
          >
            <h2 className="text-xl font-semibold">{space.name}</h2>
          </div>
        ))}
      </div>

      {selectedSpace && (
        <div className="mt-8">
          <h2 className="text-2xl font-bold mb-4">Seat Places in {selectedSpace.name}</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {seatPlaces.map((seatPlace) => (
              <div key={seatPlace.id} className="border p-4 rounded-lg">
                <p className="mb-2">Seat Place ID: {seatPlace.id}</p>
                <button
                  onClick={() => handleBooking(seatPlace.id)}
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                  Book Now
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default SpacesList;
